if(!this.action) return;

function createSample(sample) {
    var entrysample = {$content: {}}
    ui.form(`
        <div>
        <form>
            <table>
            <tr>
            <th align=right>Reference<br><span style='font-size: smaller'>Reaction code</span></th>
            <td><input type="text" name="code" pattern="[A-Za-z0-9 .-]{3,}"/></td>
            </tr>
            <tr>
            <th align=right>Batch<br><span style='font-size: smaller'>Fraction</span></th>
            <td><input type="text" name="batch" pattern="[A-Za-z0-9 .-]*"/></td>
            </tr>
            <!--
            <tr>
            <th align=right>Sample<br><span style='font-size: smaller'>(empty most of the time)</span></th>
            <td><input type="text" name="sample" pattern="[A-Za-z0-9 .-]*"/></td>
            </tr>
            -->
            </table>
            <input type="submit" value="Submit"/>
        </form>
        </div>
    `,{}).then(function(result){
        if (!result || !result.code || result.batch == null) return;
        entrysample.$id = [result.code, result.batch];
        if (result.sample) entrysample.$id.push(result.sample);
        var reference=result.code + ( result.batch ? ' '+result.batch : '');
        var userInfo = API.cache('userInfo');

        if (userInfo && userInfo.allGroups) {
            entrysample.$owners=userInfo.allGroups;
        }

        roc.create(entrysample).then(function(sample) {
            openDefault({
                uuid: sample._id,
                reference
            });
            // we do like if we would have click on the line we just added
            API.createData('sampleClicked', {id: sample._id});
        });
    });
}

function deleteSample(entry) {
    ui.confirm('Are you sure you want to delete ' + entry.value.reference)
    .then(function(res) {
        if(!res) return;
        roc.delete(entry.id);
    });
}

function refreshSample() {

    var uuid=(API.getData('sampleClicked') || {}).id;

    var options = {varName: 'samples'};
    var form = API.getData('formSample'), group;
    if(!form) return;
    group = form.group;

    window.localStorage.setItem('eln-default-sample-group', String(group));

    if(group === 'mine') {
        options.mine = 1;
    } else if(group !== 'all') {
        options.groups = group;
    }

    options.sort = function(a, b) {
        if(a.value.modificationDate > b.value.modificationDate) {
            return -1;
        } else if(a.value.modificationDate < b.value.modificationDate) {
            return 1;
        } else {
            return 0;
        }
    }

    roc.query('sample_toc', options).then(function(data) {
        API.cache('nbSamples', data.length);
        API.doAction('renderTiles');
        // we need to select the previously selected element
        const index = data.findIndex(sample => String(sample.id) == uuid);
        if (index !== -1) {
            API.doAction('clickRow', index);
        }
    });
}

function createForm(groups) {
    var possibleGroups = ["all", "mine"].concat(groups);
    var defaultGroup = window.localStorage.getItem('eln-default-sample-group');
    if(possibleGroups.indexOf(defaultGroup) === -1) {
        defaultGroup = 'all';
    }
    var schema = {
        type: 'object',
        properties: {
            group: {
                type: 'string',
                enum: possibleGroups,
                default: defaultGroup,
                required: true
            }
        }
    }
    API.createData('formSchema', schema)
}

function openDefault(sample) {
    var defaultTileURL='15c9a2dcd55c963fdedf2c18a1471b03';
    var defaultTile=API.getData('tiles').filter(a => String(a.viewURL)===defaultTileURL);
    if (defaultTile.length!==1) {
        console.log('Default tile not found !!!');
        return;
    }
    var tile=defaultTile[0];
    handleTileClick({
            tile,
            sample
        }
    );
}

function handleTileClick(value={}) {
    var {
        tile,
        sample={}
    } = value;

    var uuid = sample.uuid;
    var reference = sample.reference;

    var referer=document.location.href.replace(/(\/\/.*?\/).*/,'$1').replace('blob:','');
    var viewURL=tile.viewURL;
    viewURL+='?referer='+encodeURIComponent(referer);
    if (tile.rev) viewURL+='&rev='+tile.rev;

    switch (String(tile.kind)) {
        case 'sample':
            if (! uuid || ! reference) return;
            IframeBridge.postMessage('tab.open', {
                id: `${tile.id} ${reference}`,
                url: viewURL,
                data: {
                    printer: printerConfig,
                    couchDB,
                    uuid: String(uuid),
                    data: tile.data ? JSON.parse(tile.data) : undefined,
                    reference
                }
            });
            break;
        case 'optionalSample':
            uuid = uuid || '';
            IframeBridge.postMessage('tab.open', {
                id: tile.id,
                url: viewURL,
                data: {
                    printer: printerConfig,
                    couchDB,
                    uuid: String(uuid),
                    data: tile.data ? JSON.parse(tile.data) : undefined,
                }
            });
            break;
        case 'rawIframe':
            IframeBridge.postMessage('tab.open', {
                id: tile.id,
                url: tile.viewURL,
                rawIframe: true,
                data: {
                    printer: printerConfig,
                    couchDB,
                    uuid: String(uuid),
                    data: tile.data ? JSON.parse(tile.data) : undefined,
                }
            });
            break;
        default:
            console.warn(`unknown tile kind: ${tile.kind}`);
            break;
    }

}

var tilesData=get('tiles');
