function checkCache(val) {
    if(val) {
        const parts = val.split(':');
        if(parts[0] === 'cache') {
            const jpath = parts[1].split('.');
            const name = jpath.splice(0, 1);
            if(jpath.length === 0) return API.cache(name[0]);
            return DataObject.resurrect(DataObject.check(Object.assign({}, API.cache(name[0]))).getChildSync(jpath));
        } else {
            return val;
        }
    }
}

function revNumber(rev) {
    const m = /^(\d+)/.exec(rev);
    return m && m[1];
}

