        API.doAction('prependTile', getTileDefinition(doc));
        break;
    }
    case 'insertAfter': {
        const row = this.action.value;
        const arr = row.__parent;
        const idx = arr.indexOf(row);
        const choice = await getViewChoice();
        debugger;
        const doc = await getLatestView(choice.id);
