        let viewURL = this.action.value.viewURL;
        if(!viewURL) return;
        viewURL = String(viewURL);
        const doc = await getLatestView(viewURL);
        if(doc) {
            this.action.value.setChildSync(['rev'], doc._rev)
            ui.showNotification(`revision updated to ${doc._rev}`, 'success')
        }
        break;
    }
    default: {
        console.log('unhandled action');
    }
}

async function getViewChoice() {
