        .get('https://www.cheminfo.org/couch/cheminfo-public/_design/app/_view/docs?key=[%22c6h6%22,%20%22admin@cheminfo.org%22]')
        .set('Accept', 'application/json');

    const choice = await ui.chooseSearch(body.rows, {
        text: el => el.value.title,
        groupBy: (el) => el.value.category || 'No category'
    });
    return choice;
}

async function getLatestView(id) {
    // Get latest revision
    const {body: doc} = await superagent
        .get(`https://www.cheminfo.org/couch/cheminfo-public/${id}`)
        .set('Accept', 'application/json');
    return doc;
}

function getTileDefinition(doc) {
    return {
        id: doc.$content.name,
        rev: doc._rev,
        icon: doc.$content.icon,
        description: doc.$content.title,
        viewURL: doc._id,
        bgcolor: 'rgb(255, 255, 255)',
        empty: false
    }
}

require(['superagent', 'src/util/ui'], function(superagent, ui) {
    var couchUrl = API.cache('couchUrl');
    var database = API.cache('database');
    var roc = API.cache('rocReaction');
    if(value.$owners.indexOf('anonymousRead') !== -1) {
        makePrivate();
    } else {
        makePublic();
    }

    function getEntry(id) {
        roc.get(id);
    }

    function makePublic() {
        superagent.put(couchUrl + 'db/' + database + '/entry/' + value._id + '/_owner/anonymousRead')
            .withCredentials()
            .then(function(res) {
                if(res.status === 200) {
                    ui.showNotification('reaction was made public', 'success')
                    getEntry(value._id);
                } else {
                    ui.showNotification('make reaction public failed', 'error');
                }
            }).catch(function(err) {
                ui.showNotification('make reaction public failed', 'error');
            });
    }

    function makePrivate() {
        superagent.del(couchUrl + 'db/' + database + '/entry/' + value._id + '/_owner/anonymousRead')
            .withCredentials()
            .then(function(res) {
                if(res.status === 200) {
                    ui.showNotification('reaction was made private', 'success');
                    getEntry(value._id)
                } else {
                    ui.showNotification('make reaction private failed', 'error');
                }
            }).catch(function(err) {
                ui.showNotification('make reaction private failed', 'error');
            });
    }
});

