function handleTileClick(value={}) {
    const {
        tile,
        sample={}
    } = value;

    const uuid = sample.uuid || '';
    const reference = sample.reference;

    const referer=document.location.href.replace(/(\/\/.*?\/).*/,'$1').replace('blob:','');
    let viewURL=tile.viewURL+'?referer='+encodeURIComponent(referer);
    if (tile.rev) viewURL+='&rev='+tile.rev;
    switch (String(tile.kind)) {
