            IframeBridge.postMessage('tab.open', {
                id: `${tile.id} ${reference}`,
                url: viewURL,
                data: {
                    printer: printerConfig,
                    couchDB,
                    uuid: String(uuid),
                    data: tile.data ? JSON.parse(String(tile.data)) : undefined,
                    reference
